import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, switchMap, map } from 'rxjs';
import { YouTubeResponse } from '../models/youtube.model';

@Injectable({
  providedIn: 'root'
})
export class YoutubeService {
  private readonly API_KEY = 'AIzaSyDtPN9B5cvC2ARdE7x5qNROKcfEhc6u8NA';
  private readonly API_URL = 'https://www.googleapis.com/youtube/v3';

  constructor(private http: HttpClient) {}

  getVideosByCategory(category: string, pageToken?: string): Observable<YouTubeResponse> {
    // First, search for videos
    return this.searchVideos(category, pageToken).pipe(
      switchMap(searchResponse => {
        // Get video IDs from search results
        const videoIds = searchResponse.items.map(item => item.id.videoId).join(',');
        
        // Then get statistics for those videos
        return this.getVideoDetails(videoIds).pipe(
          map(statsResponse => {
            // Merge statistics with search results
            const items = searchResponse.items.map(searchItem => {
              const statsItem = statsResponse.items.find(
                statItem => statItem.id === searchItem.id.videoId
              );
              return {
                ...searchItem,
                statistics: statsItem?.statistics
              };
            });
            return {
              items,
              nextPageToken: searchResponse.nextPageToken
            };
          })
        );
      })
    );
  }

  private searchVideos(category: string, pageToken?: string): Observable<YouTubeResponse> {
    const params = {
      part: 'snippet',
      maxResults: '12',
      type: 'video',
      regionCode: 'IN',
      relevanceLanguage: 'ml',
      q: category,
      pageToken: pageToken || '',
      key: this.API_KEY
    };

    return this.http.get<YouTubeResponse>(`${this.API_URL}/search`, { params });
  }

  private getVideoDetails(videoIds: string): Observable<YouTubeResponse> {
    const params = {
      part: 'statistics',
      id: videoIds,
      key: this.API_KEY
    };

    return this.http.get<YouTubeResponse>(`${this.API_URL}/videos`, { params });
  }
}
