import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { YouTubeResponse } from '../models/youtube.model';

@Injectable({
  providedIn: 'root'
})
export class YoutubeService {
  private readonly API_KEY = 'AIzaSyDtPN9B5cvC2ARdE7x5qNROKcfEhc6u8NA';
  private readonly API_URL = 'https://www.googleapis.com/youtube/v3';

  constructor(private http: HttpClient) {}

  getVideosByCategory(category: string, pageToken?: string): Observable<YouTubeResponse> {
    const params = {
      part: 'snippet,statistics',
      maxResults: '12',
      type: 'video',
      regionCode: 'IN',
      relevanceLanguage: 'ml',
      q: category,
      pageToken: pageToken || '',
      key: this.API_KEY
    };

    return this.http.get<YouTubeResponse>(`${this.API_URL}/search`, { params });
  }

  getVideoDetails(videoId: string): Observable<YouTubeResponse> {
    const params = {
      part: 'snippet,statistics,player',
      id: videoId,
      key: this.API_KEY
    };

    return this.http.get<YouTubeResponse>(`${this.API_URL}/videos`, { params });
  }
}
